import R from 'ramda';
import request from 'superagent';
import disks from './mockDisks';
import categories from './mockCategories';

export const fetchDisks = async () => {
  const {body} = await request.get(
    'http://www.mocky.io/v2/5b000ec1310000780076dee8'
  )
  return body.disks;

  // for without backend 

  // return new Promise(resolve => {
  //   resolve(disks)
  // })
};

export const loadMoreDisks = async ({offset}) => {
  return new Promise(resolve => {
    resolve(disks)
  })
};

export const fetchDiskById = async id => {
  return new Promise((resolve, reject)=>{
    const disk = R.find(R.propEq('id', id), disks);
    resolve(disk);
  });
}

export const fetchCategories = async () => {
  return new Promise(resolve => {
    resolve(categories)
  })
};