import React from 'react';
import {connect} from 'react-redux';
import R from 'ramda';
import {Link} from 'react-router';
import {
  getBasketDisksWithCount,
  getTotalBasketPrice
} from 'selectors';

import {
  removeDiskFromBasket,
  basketCheckout,
  cleanBasket
} from 'actions';

const Basket = ({
  disks, 
  totalPrice, 
  removeDiskFromBasket,
  basketCheckout,
  cleanBasket
}) => {
  const isBasketEmpty = R.isEmpty(disks);

  const renderContent = () => (
    <div>
      {isBasketEmpty && <div>Your shopping cart is empty</div>}
      <div className='table-responsive'>
        <table className='table-bordered table-striped table-condensed cf'>
          <tbody>
            {disks.map((disk, index) => (
              <tr
                key={index}
                className='item-checout'
              >
                <td className='first-column-checkout'>
                  <img 
                    className='img-thumbnail'
                    src={disk.image} 
                    alt={disk.name}
                  />
                </td>
                <td>{disk.name}</td>
                <td>${disk.price}</td>
                <td>{disk.count}</td>
                <td>
                  <span 
                    onClick={() => removeDiskFromBasket(disk.id)}
                    className='delete-cart'></span>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      {
        R.not(isBasketEmpty) &&
          <div className='row'>
            <div className='pull-right total-user-checkout'>
              <b>Total:</b>
              ${totalPrice}
            </div>
          </div>
      }
    </div>
  )

  const renderSidebar = () => (
    <div>
      <Link
        to='/'
        className='btn btn-info'
      >
        <span className='glyphicon glyphicon-info-sign'/>
        <span>Continue shopping</span>
      </Link>
      {
        R.not(isBasketEmpty) &&
        <div>
          <button
            onClick={cleanBasket}
            className='btn btn-danger'
          >
            <span className='glyphicon glyphicon-trash' />
            Clear cart
          </button>
          <button
            className='btn btn-success'
            onClick={() => basketCheckout(disks)}
          >
            <span className='glyphicon glyphicon-envelope' />
            Checkout
          </button>
        </div>
      }
    </div>
  )
  return (
    <div className='view-container'>
      <div className='container'>
        <div className='row'>
          <div className='col-md-9'>
            {renderContent()}
          </div>
          <div className='col-md-3 btn-user-checkout'>
            {renderSidebar()}
          </div>
        </div>
      </div>
    </div>
  )
}

const mapStateToProps = state => ({
  disks: getBasketDisksWithCount(state),
  totalPrice: getTotalBasketPrice(state)
})

const mapDispatchToProps = {
  removeDiskFromBasket,
  basketCheckout,
  cleanBasket
}

export default connect(mapStateToProps, mapDispatchToProps)(Basket);