import React, {Component} from 'react';
import {connect} from 'react-redux';
import R from 'ramda';
import {Link} from 'react-router';
import {fetchDisks, loadMoreDisks, addDiskToBasket, fetchCategories} from 'actions';
import {getDisks} from 'selectors';

class Disks extends Component {
  componentDidMount() {
    this.props.fetchDisks()
    this.props.fetchCategories()
  }

  renderDisk (disk,index) {
    const {addDiskToBasket} = this.props;
    const shortDescription = `${R.take(60, disk.description)}...`;
    return (
      <div className='col-sm-4 col-lg-4 col-md-4 book-list' key={index}>
        <div className='thumbnail'>
          <img  
            src={disk.image} 
            alt={disk.name}
          />
          <div className='caption'>
            <h4 className='pull-right'>
              ${disk.price}
            </h4>
            <h4>
              <Link to={`/disks/${disk.id}`}>
                {disk.name}
              </Link>
            </h4>
            <p>{shortDescription}</p>
            <p className='itemButton'>
              <button 
                className='btn btn-primary'
                onClick={() => addDiskToBasket(disk.id)}
              >
                Buy now!
              </button>
              <Link
                to={`/disks/${disk.id}`}
                className='btn btn-info'
              >
                More info
              </Link>
            </p>
          </div>
        </div>
      </div>
    )
  }

  render () {
    const {disks, loadMoreDisks} = this.props;
    return (
      <div>
        <div className='books row'>
          {disks.map((disk, index) => this.renderDisk(disk, index))}
        </div>
        <div className='row'>
          <div className='col-md-12'>
            <button 
              className='pull-right btn btn-primary'
              onClick={loadMoreDisks}
            >
              Load More
            </button>
          </div>
        </div>
      </div>
    )
  }
}  

const mapStateToProps = (state, ownProps) => {
  return {
    disks: getDisks(state, ownProps)
  }
};

const mapDispathToProps = {
   fetchDisks,
   loadMoreDisks,
   addDiskToBasket,
   fetchCategories
}

export default connect(mapStateToProps, mapDispathToProps)(Disks);