import R from 'ramda';

import {
  FETCH_DISKS_SUCCESS,
  LOAD_MORE_DISKS_SUCCESS,
  SEARCH_DISK
} from 'actionTypes';

const initialState = {
  ids: [],
  search: ''
}

export default (state = initialState, {type, payload}) => {
  switch (type) {
    case FETCH_DISKS_SUCCESS:
      return R.merge(state, {
        ids: R.pluck('id', payload)
      })
    case LOAD_MORE_DISKS_SUCCESS:
      const ids = R.pluck('id', payload)
      return R.merge(state, {
        ids: R.concat(ids, state.ids)
      })
    case SEARCH_DISK:
      return R.merge(state, {
        search: payload
      })

    default:
      return state
  }
}