import R from 'ramda';

export const getDiskById = (state, id) => R.prop(id, state.disks);
export const getActiveCategoryId = ownProps => R.path(['params', 'id'], ownProps);

export const getDisks = (state, ownProps) => {
  const activeCategoryId = getActiveCategoryId(ownProps);
  const applySearch = item => R.contains(
    state.disksPage.search,
    R.prop('name', item)
  )

  const applyCategory = item => R.equals(
    activeCategoryId,
    R.prop('categoryId', item)
  )

  const disks = R.compose(
    R.filter(applySearch),
    R.when(R.always(activeCategoryId), R.filter(applyCategory)),
    R.map(id => getDiskById(state, id))
  )(state.disksPage.ids)

  return disks;
}

export const getRenderedDisksLength = state => R.length(state.disksPage.ids);
export const getTotalBasketCount = state => R.length(state.basket);

export const getTotalBasketPrice = state => {
  const totalPrice = R.compose(
    R.sum,
    R.pluck('price'),
    R.map(id => getDiskById(state, id))
  )(state.basket);

  return totalPrice;
}

export const getCategories = state => R.values(state.categories);

export const getBasketDisksWithCount = state => {
  const uniqueIds = R.uniq(state.basket);
  const diskCount = id => R.compose(
    R.length,
    R.filter(basketId => R.equals(id, basketId))
  )(state.basket);
  const diskWithCount = disk => R.assoc('count', diskCount(disk.id), disk);
  const disks = R.compose(
    R.map(diskWithCount),
    R.map(id => getDiskById(state, id))
  )(uniqueIds);
  return disks;
};